<form action='/login' method='post' id='login-form' class='myForm text-center needs-validation'><div class='form-group form-item-parent direction-rtl'><i class='fas fa-lock'></i><input class='myInput text-center' name='new-password' type='password' id='password' placeholder='رمز عبور جدید' required></div><div class='form-group form-item-parent direction-rtl'><i class='fas fa-lock'></i><input class='myInput text-center' name='new-password-repeat' type='password' id='password' placeholder='تکرار رمز عبور جدید' required></div><button type='button' id='submit-new-password' class='butt login-submit'>ورود</button></form>
